$(function(){$(".collapsable li:regex(class,(submission|packlet)) header a").live("click",function(a){a.preventDefault(),$(this).parents("li:regex(class,(submission|packlet))").toggleClass("collapsed")}),$(".schedulable li:regex(class,(submission|packlet))").draggable({handle:"span.drag-handle-wrap",axis:"y",stack:"article",zIndex:1,helper:function(){return $(this).clone().css("width",this.offsetWidth).first()}}),$("article#submissions section").droppable({hoverClass:"drop-here",drop:function(a,b){li=b.draggable,section=$(this),section.hasClass("unscheduled")&&li.hasClass("packlet")?(packlet_id=li.attr("id").split("_")[1],$.ajax({type:"POST",dataType:"script",url:"/packlets/"+packlet_id,data:{_method:"delete"}})):li.parents("section").attr("id")!=section.attr("id")&&(li.hasClass("submission")?$.ajax({type:"POST",dataType:"script",url:"/packlets",data:{submission:li.attr("id").split("_").pop(),meeting:section.attr("id").split("_").pop()}}):$.ajax({type:"POST",dataType:"script",url:"/packlets",data:{packlet:li.attr("id").split("_")[1],meeting:section.attr("id").split("_")[1]}}))}})})